import React, { useEffect } from 'react'
import { useState } from 'react'

export default function Navbar(props) {
    const [input,setinput]= useState("")
const {handleSearch,MovieData}= props


    

    const handleChange=(e)=>{
        let value= e.target.value
       /// setinput(value)
       async function Movie() {
        const response = await fetch(`https://api.themoviedb.org/3/search/movie?api_key=61a165cb4b595b07d284dbedb9ee6473&query=${value}`)
        const responseData = await response.json();
       // setMovieData(responseData.results)
       console.log(responseData);
    }

    }
  
  return (
    <div>
            <input type = "textbar" placeholder='Search for Movies' onChange={handleChange}value={input}/>
                <button onClick ={(e)=>handleSearch(e)}>Search</button>
          
     
    </div>
  )
}
