import React, { useState } from 'react'
import './Css files/moviesearch.css'
export default function MovieSearch(props) {
  const { Title, Poster, Type,Year } = props.DataTrans
  const  handleLike  = props.handleLike
  const favMovie= props.favMovie
  const handleMovie= props.handleMovie
  // const [like,islike]=useState(false)
  // const handleFavourite =()=>{
  //   handleLike(props.DataTrans)
  //   islike(!like)
  // }
  return (
    <>

      <div className='Movie-Body'>
        <div className='Movie-info'><img src={Poster} className='image' alt={Title} /> </div>
     <div className='Description'>   <h3>{Title}</h3>
         <p>{Type}</p> 
         <p>{Year}</p>
        
         

      {!like?<button onClick={handleMovie}>Like</button>:
         <button onClick={(e)=>handleLike(props.DataTrans,islike)}>unLike</button>}
      </div>
      </div>

    </>
  )
}
