import React, { useEffect, useState } from 'react'
import MovieSearch from './MovieSearch'
import Navbar from './Navbar'
export default function MovieData() {
    const [MovieData, setMovieData] = useState([])
    const [like,setlike]= useState([])
    const [isfavMovie,favMovie]= useState(false)

    async function Movie(s) {
        const response = await fetch(`http://www.omdbapi.com/?apikey=891a9115&s=${s}`)
        const responseData = await response.json();
       
        setMovieData(responseData.Search)
    }

    useEffect(() => {
         
        Movie('dhoom')
    },[])
  
    const handleSearch=(e,input)=>{
      e.preventDefault()
      console.log("input",input)
           Movie(input);
    }
    const handleLike=(Movie)=>{
   let list = [...like,Movie]
setlike(list)
    }

  const handleFav=()=>{
    isfavMovie(true)
  }
const handleMovie=()=>{
    isfavMovie(false)
}

    return (
        <div>
          <Navbar handleSearch={handleSearch} />
          <div>  <button onClick={handleMovie}>Movie</button>
           <button onClick={handleFav}>Fav</button></div> 

            {MovieData && MovieData.map((item) => {
           
                return(
                    <div> 
          <MovieSearch     DataTrans={item} 
          handleLike={handleLike}
          />
        </div>
            )})}
          
        </div>
    )}
