import React from 'react'
import { useState } from 'react'

export default function Navbar(props) {
    const [input,setinput]= useState("")
const {handleSearch,MovieData}= props

    const handleChange=(e)=>{
        let value= e.target.value
        setinput(value)
    }
  return (
    <div>
          
            {MovieData&&MovieData.map((item)=>{
                return(
                <div>{item.title}{item.id}
                <input type = "textbar" placeholder='Search for Movies' onChange={handleChange}value={input}/>
                <button onClick ={(e)=>handleSearch(e,id)}>Search</button></div>
            )})}
    </div>
  )
}
